/* --- My Custom CSS --- */
/* vvvvvvvvvvvvvvvvvvvvv */

/* Use pointer cursor */
.Tab {
	cursor: pointer;
}

/* Unpinned tabs */
.Tab[data-pin="false"] {

	/* Container indicator */
	.ctx {
		width: 4px;
		height: 16px;
		top: 50%;
		transform: translateY(-50%);
		right: -8px;
		border-radius: 999px;
	}
}

/* Pinned tabs */
.Tab[data-pin="true"] {
	--tabs-border-radius: 8px;
	--tabs-normal-bg: rgba(255,255,255,0.07);

	/* Container indicator */
	.ctx {
		bottom: 4px !important;
		left: 8px !important;
		right: 8px !important;
		width: auto !important;
		height: 3px !important;
		border-radius: 8px;
	}
}
